import React from "react";
import "./Area.css";

class Area extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      areaId: 0,
      cities: [
        { id: 0, city: ["서울1", "서울2", "서울3", "서울4", "서울5", "서울6"] },
        { id: 1, city: ["경기1", "경기2", "경기3", "경기4", "경기5", "경기6"] },
        { id: 2, city: ["인천1", "인천2", "인천3", "인천4", "인천5", "인천6"] },
        { id: 2, city: ["강원1", "강원2", "강원3", "강원4", "강원5", "강원6"] },
      ],
      activeId: null,
    };
  }

  handleClick(event, id) {
    this.setState({ activeId: id });
    this.props.onSlected(id);
  }

  render() {
    return (
      <ul className="area-list">
        {this.state.areas.map((list) => (
          <li
            key={list.id}
            className={list.id === this.state.activeId && "is-active"}
            onClick={() => this.handleClick(this, list.id)}
          >
            {list.area}
          </li>
        ))}
      </ul>
    );
  }
}

export default Area;
