import React from "react";
import "./Area.css";

class Area extends React.Component {
  constructor(props) {
    super(props);
    console.log(this.props.id);
    this.state = {
      areaId: this.props.id,
      cities: [
        { id: 0, city: ["서울1", "서울2", "서울3", "서울4", "서울5", "서울6"] },
        { id: 1, city: ["경기1", "경기2", "경기3", "경기4", "경기5", "경기6"] },
        { id: 2, city: ["인천1", "인천2", "인천3", "인천4", "인천5", "인천6"] },
        { id: 2, city: ["강원1", "강원2", "강원3", "강원4", "강원5", "강원6"] },
      ],
      activeId: 0,
    };
  }

  handleClick(id) {
    this.setState({ activeId: id });
  }

  render() {
    var data = this.state.cities[this.state.areaId];
    console.log(this.areaId);
    console.log(this.state.cities);
    return (
      //도시를 수정해야 함.
      <ul className="city-list">
        {data.map((list) => (
          <li
            key={list.id}
            className={list.id === this.state.activeId ? "is-active" : " "}
            onClick={function (id) {
              this.handleClick(id);
              this.props.onSlected(id);
            }.bind(this, list.id)}
          >
            {list.area}
          </li>
        ))}
      </ul>
    );
  }
}

export default Area;
