import React from "react";
import "./Area.css";

class Area extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      areas: [
        { id: 0, area: "서울", isslected: false },
        { id: 1, area: "경기", isslected: false },
        { id: 2, area: "인천", isslected: false },
        { id: 3, area: "강원", isslected: false },
      ],
      activeId: null,
    };
  }

  handleClick(event, id) {
    this.setState({ activeId: id });
  }

  render() {
    return (
      <ul className="area-list">
        {this.state.areas.map((list) => (
          <li
            key={list.id}
            className={list.id === this.state.activeId && "is-active"}
            onClick={() => this.handleClick(this, list.id)}
          >
            {list.area}
          </li>
        ))}
      </ul>
    );
  }
}

export default Area;
