import React from "react";
import "./Area.css";

class Area extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      areaId: this.props.area_id,
      cities: [
        {
          id: 0,
          city: [
            { city_id: 0, value: "서울1" },
            { city_id: 1, value: "서울2" },
            { city_id: 2, value: "서울3" },
            { city_id: 3, value: "서울4" },
            { city_id: 4, value: "서울5" },
            { city_id: 5, value: "서울6" },
          ],
        },
        {
          id: 1,
          city: [
            { city_id: 0, value: "경기1" },
            { city_id: 1, value: "경기2" },
            { city_id: 2, value: "경기3" },
            { city_id: 3, value: "경기4" },
            { city_id: 4, value: "경기5" },
            { city_id: 5, value: "경기6" },
          ],
        },
        {
          id: 2,
          city: [
            { city_id: 0, value: "인천1" },
            { city_id: 1, value: "인천2" },
            { city_id: 2, value: "인천3" },
            { city_id: 3, value: "인천4" },
            { city_id: 4, value: "인천5" },
            { city_id: 5, value: "인천6" },
          ],
        },
        {
          id: 3,
          city: [
            { city_id: 0, value: "강원1" },
            { city_id: 1, value: "강원2" },
            { city_id: 2, value: "강원3" },
            { city_id: 3, value: "강원4" },
            { city_id: 4, value: "강원5" },
            { city_id: 5, value: "강원6" },
          ],
        },
      ],
      activeId: 0,
    };
  }

  handleClick(id) {
    this.setState({ activeId: id });
  }

  render() {
    return (
      <ul>
        {this.state.cities[this.state.areaId].city.map((list) => (
          <li
            key={list.city_id}
            className={list.city_id === this.state.activeId ? "is-active" : " "}
            onClick={function (id) {
              this.setState({ activeId: id });
              this.props.onSlected(id);
            }.bind(this, list.city_id)}
          >
            {list.value}
          </li>
        ))}
      </ul>
    );
  }
}

export default Area;
